const util = require('util');
const snippets = require('../snippets');

module.exports = function(RED) {
    function Algorithm(config) {
        RED.nodes.createNode(this, config);
        var node = this;
        script ="";
        featuremap =  config.fmethod==="ZZFeatureMap"?util.format(snippets.ZZ_MAP,config.feature_dimension):util.format(snippets.PAULI_MAP,config.feature_dimension);
        ansatz = config.amethod==="RealAmplitudes"?util.format(snippets.RA,config.num_qubits,config.areps,config.entanglement):util.format(snippets.EF,config.num_qubits,config.areps,config.entanglement);
        node.on('input', function(msg) {
            script = util.format(snippets.QNN,featuremap,ansatz,config.num_qubits);
            msg.payload+=script;
            node.send(msg);
        });
    }

    RED.nodes.registerType("algorithm", Algorithm);
}
